import React, { useState, useEffect } from 'react'
import axios from 'axios'



const ProductList = () => {
    const [daftarAlat,setDaftarAlat] = useState([]);

    useEffect(() => {
        getAlat();
    },[]);

    const getAlat = async() => {
        try{
            const response = await axios.get('http://localhost:5000/alat');
            // console.log(response.data);
            setDaftarAlat(response.data);
        } catch(error) {
            console.log(error);
        }
        
    }

    console.log(daftarAlat);


    return (
        <div>
            <section className='productList'>
                <div className='container'>
                    <div className="cards">
                        {
                            daftarAlat.map((card,i)=>(
                                <div key={i} className="card">
                                    <figure className='image is-200x200'>
                                        <img src={card.url} alt="" />
                                    </figure>
                                    <h3>{card.namaAlat}</h3>
                                    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Incidunt, obcaecati!</p>
                                </div>
                            ))
                        }
                    </div>
                </div>
            </section>
        </div>
    )

}
export default ProductList
